nasal.open:
var dlg = props.globals.getNode("/sim/gui/dialogs/route-manager", 1);

var load_route1 = func(path) {
    route.Polygon.load("1",path.getValue());
}
var save_route1 = func(path) {
    route.Polygon.save("1",path.getValue());
}
